    private val generatedPacks = mutableListOf<WordPack>()

    fun getAll(): List<WordPack> = listOf(
        default, extended, words400,
        general, animals, movies, celebrities, food, professions, countries, actions
    ) + generatedPacks

    fun getById(id: String): WordPack? = getAll().find { it.id == id }

    fun getByIds(ids: List<String>): List<WordPack> = ids.mapNotNull { getById(it) }

    fun addGeneratedPack(wordPack: WordPack) {
        generatedPacks.add(wordPack)
    }

    fun removeGeneratedPack(id: String) {
        generatedPacks.removeAll { it.id == id }
    }
